mapper.update:
UPDATE educloud_user SET campus_id = #{campusId}
WHERE user_id IN

    #{userId}

mapper.select:
SELECT `user`.user_id,`user`.nickname,`user`.phone,`user`.`status`,`user`.register_time,`user`.campus_id,`user`.source_id , tmp_grade.gradeName grade, tmp_role.roleName role FROM educloud_user user LEFT JOIN educloud_user_grade userGrade ON userGrade.user_id = `user`.user_id LEFT JOIN educloud_user_role userRole ON userRole.user_id = `user`.user_id LEFT JOIN ( SELECT userGrade.user_id,GROUP_CONCAT(grade.grade_name ORDER BY grade.sort ASC SEPARATOR '、') gradeName FROM educloud_user_grade userGrade,educloud_system.educloud_grade grade WHERE userGrade.grade_id = grade.grade_id GROUP BY userGrade.user_id ) tmp_grade ON `user`.user_id = tmp_grade.user_id LEFT JOIN ( SELECT userRole.user_id, GROUP_CONCAT(role.role_name ORDER BY role.role_id ASC SEPARATOR '、') roleName FROM educloud_user_role userRole,educloud_role role WHERE userRole.role_id = role.role_id GROUP BY userRole.user_id ) tmp_role ON `user`.user_id = tmp_role.user_id
AND `user`.phone LIKE '%${userQuery.phone}%'
AND `user`.nickname LIKE '%${userQuery.nickname}%'
#{gradeId}
#{roleId}
#{campusId}
#{sourceId}
#{status}
GROUP BY `user`.user_id
ORDER BY register_time ASC
ORDER BY register_time DESC
